mapper.select:

WHERE TRUE

ORDER BY eno DESC
LIMIT #{offset}, #{pageSize}
;

mapper.update:
update employee
set name = #{name}, number = #{number}, position = #{position}, team = #{team}
where eno = #{eno};

mapper.select:
select tno, name from team where true
AND name like concat('%',#{keyword},'%')
order by tno desc limit #{offset} , #{pageSize}

select pno, name from position where true
AND name like concat('%',#{keyword},'%')
order by seq desc limit #{offset} , #{pageSize}
